\COPY (SELECT operator_id, publiccode, name FROM line WHERE operator_id like 'HTM:%' OR operator_id like 'VTN:%' OR operator_id like 'ARR:%') TO '/tmp/openebs_lines.csv' WITH CSV HEADER;
\COPY (SELECT operator_id, name, latitude, longitude FROM stoppoint WHERE operator_id like 'HTM:%' OR operator_id like 'VTN:%' OR operator_id like 'ARR:%') TO '/tmp/openebs_stops.csv' WITH CSV HEADER;
\COPY (SELECT DISTINCT j.privatecode as journey_id, j.departuretime as base_departure_time, p.directiontype as direction FROM servicejourney as j JOIN journeypattern as p on (j.journeypatternref = p.id) WHERE j.privatecode like 'HTM:%' or j.privatecode like 'VTN:%' OR j.privatecode like 'ARR:%' ORDER BY journey_id) TO '/tmp/openebs_journeys.csv' WITH CSV HEADER;
\COPY (SELECT distinct on (privatecode, validdate) privatecode, validdate FROM servicejourney JOIN availabilityconditionday USING (availabilityconditionref) WHERE (privatecode like 'HTM:%' OR privatecode like 'VTN:%' or privatecode like 'ARR:%') and isavailable = true and validdate >= 'yesterday' ORDER BY privatecode) TO '/tmp/openebs_journey_dates.csv' WITH CSV HEADER;